  //   // console.log("check-row",rows);
  //   return rows
  // } catch (error) {
  //  console.log("check" ,error); 
  // }



}

module.exports = {
  createNewUser
  , getUserList,
  deleteUser,
  getUserbyId,
  updateUserInfor
}

